import { Component, OnInit, ViewEncapsulation } from '@angular/core';
declare var jQuery: any;
@Component({
  selector: 'app-professor-profile-settings',
  templateUrl: './professor-profile-settings.component.html',
  styleUrls: ['./professor-profile-settings.component.css'],
  encapsulation: ViewEncapsulation.None
})
export class ProfessorProfileSettingsComponent implements OnInit {

  constructor() { }
  tagitemsSpecialization = [
    'Study Care', 'Course List'
  ];
  ngOnInit(): void {
    
(function($) {
  "use strict";

// Pricing Options Show

$('#pricing_select input[name="rating_option"]').on('click', function() {
  if ($(this).val() == 'price_free') {
    $('#custom_price_cont').hide();
  }
  if ($(this).val() == 'custom_price') {
    $('#custom_price_cont').show();
  }
  else {
  }
});

// Education Add More

  $(".education-info").on('click','.trash', function () {
  $(this).closest('.education-cont').remove();
  return false;
  });

  $(".add-education").on('click', function () {
  
  var educationcontent = '<div class="row form-row education-cont">' +
    '<div class="col-12 col-md-10 col-lg-11">' +
      '<div class="row form-row">' +
        '<div class="col-12 col-md-6 col-lg-4">' +
          '<div class="form-group">' +
            '<label>Degree</label>' +
            '<input type="text" class="form-control">' +
          '</div>' +
        '</div>' +
        '<div class="col-12 col-md-6 col-lg-4">' +
          '<div class="form-group">' +
            '<label>College/Institute</label>' +
            '<input type="text" class="form-control">' +
          '</div>' +
        '</div>' +
        '<div class="col-12 col-md-6 col-lg-4">' +
          '<div class="form-group">' +
            '<label>Year of Completion</label>' +
            '<input type="text" class="form-control">' +
          '</div>' +
        '</div>' +
      '</div>' +
    '</div>' +
    '<div class="col-12 col-md-2 col-lg-1"><label class="d-md-block d-sm-none d-none">&nbsp;</label><a href="#" class="btn btn-danger trash"><i class="far fa-trash-alt"></i></a></div>' +
  '</div>';
  
      $(".education-info").append(educationcontent);
      return false;
  });

// Experience Add More

  $(".experience-info").on('click','.trash', function () {
  $(this).closest('.experience-cont').remove();
  return false;
  });

  $(".add-experience").on('click', function () {
  
  var experiencecontent = '<div class="row form-row experience-cont">' +
    '<div class="col-12 col-md-10 col-lg-11">' +
      '<div class="row form-row">' +
        '<div class="col-12 col-md-6 col-lg-4">' +
          '<div class="form-group">' +
            '<label>University Name</label>' +
            '<input type="text" class="form-control">' +
          '</div>' +
        '</div>' +
        '<div class="col-12 col-md-6 col-lg-4">' +
          '<div class="form-group">' +
            '<label>From</label>' +
            '<input type="text" class="form-control">' +
          '</div>' +
        '</div>' +
        '<div class="col-12 col-md-6 col-lg-4">' +
          '<div class="form-group">' +
            '<label>To</label>' +
            '<input type="text" class="form-control">' +
          '</div>' +
        '</div>' +
        '<div class="col-12 col-md-6 col-lg-4">' +
          '<div class="form-group">' +
            '<label>Designation</label>' +
            '<input type="text" class="form-control">' +
          '</div>' +
        '</div>' +
      '</div>' +
    '</div>' +
    '<div class="col-12 col-md-2 col-lg-1"><label class="d-md-block d-sm-none d-none">&nbsp;</label><a href="#" class="btn btn-danger trash"><i class="far fa-trash-alt"></i></a></div>' +
  '</div>';
  
      $(".experience-info").append(experiencecontent);
      return false;
  });

// Awards Add More

  $(".awards-info").on('click','.trash', function () {
  $(this).closest('.awards-cont').remove();
  return false;
  });

  $(".add-award").on('click', function () {

      var regcontent = '<div class="row form-row awards-cont">' +
    '<div class="col-12 col-md-5">' +
      '<div class="form-group">' +
        '<label>Awards</label>' +
        '<input type="text" class="form-control">' +
      '</div>' +
    '</div>' +
    '<div class="col-12 col-md-5">' +
      '<div class="form-group">' +
        '<label>Year</label>' +
        '<input type="text" class="form-control">' +
      '</div>' +
    '</div>' +
    '<div class="col-12 col-md-2">' +
      '<label class="d-md-block d-sm-none d-none">&nbsp;</label>' +
      '<a href="#" class="btn btn-danger trash"><i class="far fa-trash-alt"></i></a>' +
    '</div>' +
  '</div>';
  
      $(".awards-info").append(regcontent);
      return false;
  });

// Membership Add More

  $(".membership-info").on('click','.trash', function () {
  $(this).closest('.membership-cont').remove();
  return false;
  });

  $(".add-membership").on('click', function () {

      var membershipcontent = '<div class="row form-row membership-cont">' +
    '<div class="col-12 col-md-10 col-lg-5">' +
      '<div class="form-group">' +
        '<label>Memberships</label>' +
        '<input type="text" class="form-control">' +
      '</div>' +
    '</div>' +
    '<div class="col-12 col-md-2 col-lg-2">' +
      '<label class="d-md-block d-sm-none d-none">&nbsp;</label>' +
      '<a href="#" class="btn btn-danger trash"><i class="far fa-trash-alt"></i></a>' +
    '</div>' +
  '</div>';
  
      $(".membership-info").append(membershipcontent);
      return false;
  });

// Registration Add More

  $(".registrations-info").on('click','.trash', function () {
  $(this).closest('.reg-cont').remove();
  return false;
  });

  $(".add-reg").on('click', function () {

      var regcontent = '<div class="row form-row reg-cont">' +
    '<div class="col-12 col-md-5">' +
      '<div class="form-group">' +
        '<label>Registrations</label>' +
        '<input type="text" class="form-control">' +
      '</div>' +
    '</div>' +
    '<div class="col-12 col-md-5">' +
      '<div class="form-group">' +
        '<label>Year</label>' +
        '<input type="text" class="form-control">' +
      '</div>' +
    '</div>' +
    '<div class="col-12 col-md-2">' +
      '<label class="d-md-block d-sm-none d-none">&nbsp;</label>' +
      '<a href="#" class="btn btn-danger trash"><i class="far fa-trash-alt"></i></a>' +
    '</div>' +
  '</div>';
  
      $(".registrations-info").append(regcontent);
      return false;
  });

})(jQuery);
  }
  files: File[] = [];
  onSelect(event: { addedFiles: any; }) {
    this.files.push(...event.addedFiles);
  }

  onRemove(event: File) {
    this.files.splice(this.files.indexOf(event), 1);
  }
}
