import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  slideIndex= 1;

  // tslint:disable-next-line:typedef
  plusSlides(n: any){
    this.currentSlide(n)
  };
  // tslint:disable-next-line:typedef
  currentSlide(n: any) {
    // tslint:disable-next-line:indent
  	this.showSlides(this.slideIndex = n);
}
  // tslint:disable-next-line:typedef
  showSlides(ne: any){
    var i;
    var slides = document.getElementsByClassName("mySlides");
    var dots = document.getElementsByClassName("testi-user-img");
    if (ne > slides.length) {this.slideIndex = 1}
    if (ne < 1) {this.slideIndex = slides.length}
    for (i = 0; i < slides.length; i++) {
      slides[i].classList.add("d-none");
      slides[i].className = slides[i].className.replace("d-block", "d-none");
    }
    for (i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
    }
    slides[this.slideIndex-1].classList.add("d-block");
    dots[this.slideIndex-1].className += " active";
  }
  coursesCarouselOptions = {
    loop:true,
		margin:15,
		nav:true,
		navText: ['<span class="fas fa-chevron-left"></span>','<span class="fas fa-chevron-right"></span>'],
		responsive:{
			0:{
				items:1
			},
			500:{
				items:1
			},
			768:{
				items:2
			},
			1000:{
				items:3
			},
			1300:{
				items:4
			}
		}
  };
  popularcoursesCarouselOptions = {
    loop:true,
		margin:15,
		nav:true,
		navText: ['<span class="fas fa-chevron-left"></span>','<span class="fas fa-chevron-right"></span>'],
		responsive:{
			0:{
				items:1
			},
			500:{
				items:1
			},
			768:{
				items:1
			},
			1000:{
				items:2
			},
			1300:{
				items:2
			}
		}
  };
  coursesCarouselslides = [
    {
      img: "assets/img/universities/c1.jpg",
      course: "Design",
      count: "19 Courses",
      img2: "assets/img/universities/c5.jpg",
      course2: "Business",
      count2: "19 Courses",
    },
    {
      img: "assets/img/universities/c2.jpg",
      course: "Software Development",
      count: "17 Courses",
      img2: "assets/img/universities/c6.jpg",
      course2: "Web Development",
      count2: "17 Courses",
    },
    {
      img: "assets/img/universities/c3.jpg",
      course: "Photography",
      count: "14 Courses",
      img2: "assets/img/universities/c7.jpg",
      course2: "Marketing",
      count2: "14 Courses",
    },
    {
      img: "assets/img/universities/c4.jpg",
      course: "3D + Animation",
      count: "15 Courses",
      img2: "assets/img/universities/c8.jpg",
      course2: "Audio + Music",
      count2: "15 Courses",
    },
    {
      img: "assets/img/universities/c1.jpg",
      course: "Design",
      count: "19 Courses",
      img2: "assets/img/universities/c5.jpg",
      course2: "Business",
      count2: "19 Courses",
    },
    {
      img: "assets/img/universities/c2.jpg",
      course: "Software Development",
      count: "17 Courses",
      img2: "assets/img/universities/c6.jpg",
      course2: "Web Development",
      count2: "17 Courses",
    },
  ];
  constructor() { }

  ngOnInit(): void {
    this.showSlides(this.slideIndex)
  }

}
